class MethodDrawHelper {
  static String decodeBase64(String data) {
    return String.fromCharCodes(base64Decode(data));
  }

  static String encodeBase64(String data) {
    var content = utf8.encode(data);
    var digest = base64Encode(content);
    return digest;
  }

  static const double TIME_TO_DISTANCE = 1; //时间到距离的映射
  static const double METHOD_H = 50; //叶子节点默认一个方法的高度
  static const double ZERO_TIME_METHOD_W =
      3; //时间为0的方法块的对应的宽，那么有时间的叶子节点的方法块的宽度就应该是：时间 * _TIME_TO_DISTANCE + _ZERO_TIME_METHOD_W
  static const double RIGHT_PADDING = 2;//超出计算得到的部分，为了能看清色块的范围
  static const double TOP_PADDING = 2;//第一个child距离顶部的距离


  List<CallDrawItem> _items;
  int _maxSize = 1000;
  int _delStep = 50;
  int _showTopIndex = 0; //顶部显示的索引
  int _showBottomIndex; //底部显示的索引
  double _screenW;
  double _screenH;
  double _innerTop;

  MethodDrawHelper() {
    _items = List();
  }

  CallDrawItem getItem(int index) {
    if (index >= 0 && index < _items.length) {
      return _items[index];
    } else {
      return null;
    }
  }

  int getItemSize() {
    return _items == null ? 0 : _items.length;
  }

  set screenH(double value) {
    _screenH = value;
  }

  set screenW(double value) {
    _screenW = value;
  }

  /// 添加一个记录
  addCall(CallDrawItem callDrawItem) {
    if (_items.length >= _maxSize || callDrawItem == null) {
      return;
    }
    _items.add(callDrawItem);
  }

  ///删除特定范围的数据
  remove(int start, int end) {
    if (start >= 0 && start <= end && end < _items.length) {
      _items.removeRange(start, end);
    }
  }

  ///dy > 0:向上  dy < 0:向下
  scroll(double dy) {
    if (_items == null || _items.length == 0) {
      return;
    }
    CallDrawItem callDrawItem = _items[_showTopIndex];
    callDrawItem.measure();
    double willTop = callDrawItem.top + dy;

    if (willTop < 0 &&
        willTop.abs() > callDrawItem.h &&
        _showTopIndex + 1 < _items.length) {
      //往上滑动
      _showTopIndex++;
      CallDrawItem next = _items[_showTopIndex];
      next.measure();
      next.top = willTop + callDrawItem.h;
    } else if (willTop > 0 && _showTopIndex - 1 >= 0) {
      //往下滑动
      _showTopIndex--;
      CallDrawItem pre = _items[_showTopIndex];
      pre.measure();
      pre.top = willTop.abs() - pre.h;
    } else {
      callDrawItem.top = willTop;
    }
  }

  int getDrawSize() {
    return _items.length;
  }

  ///开始绘制代码块
  draw(Canvas canvas, Paint paint) {
    if (canvas == null || paint == null) {
      return;
    }
    if (_showTopIndex >= 0 && _showTopIndex < _items.length) {
      int index = _showTopIndex;
      double nextTop = 0;
      CallDrawItem next = _items[index];
      nextTop = next.top;
      while (_isVisible(next.top)) {
        next.performDraw(canvas, nextTop, paint);
        index++;
        if (index >= _items.length) {
          break;
        }

        //计算下一个需要绘制的代码块的头部位置
        nextTop += next.h;
        //下一个需要绘制的代码块
        next = _items[index];
        //设置头部位置
        next.top = nextTop;
      }
      _showBottomIndex = index - 1;
    }
  }

  bool _isVisible(double top) {
    return top <= _screenH;
  }

  ///避免数据太多，删除最久的
  avoidTooMuch() {
    int len = _items == null ? 0 : _items.length;
    if (len > _maxSize) {
      //开始删除

    }
  }

  ///清除数据
  clear(){
    _items.clear();
  }
}
